mapper.select:
select *
from admin
where account = #{account }
  and password = #{password}

select *
from viewresult
order by subsidize_id limit #{startIndex}, #{pageSize}

select *
from viewApply
order by time desc limit #{startIndex}, #{pageSize}

select count(*)
from admin
where admin_id = #{adminId}
  and password = #{pwd}

mapper.update:
update admin
set password=#{chg}
where admin_id = #{adminId}
