mapper.select:
select *
from student
order by student_id limit #{startIndex}, #{pageSize}

select *
from viewapply
where sequence = #{sq} limit #{va.startIndex}
    , #{va.pageSize}

mapper.update:
update apply
set illustrate=#{illustrate},
    time=#{time},
    remark=#{remark}
where apply_id = #{applyId}

mapper.select:
select *
from viewresult
order by subsidize_id limit #{startIndex}, #{pageSize}

select count(*)
from student
where student_id = #{id}
  and password = #{pwd};

mapper.update:
update student
set password=#{chg}
where student_id = #{id}

mapper.select:
select count(*) as value
from viewApply
where sequence =#{sequence}
