mapper.select:
select * from teacher


        and workId like '%' #{workId} '%'


        and password = #{password}


        and name like '%' #{name} '%'


        and faculty = #{faculty}

select *
from teacher
order by id limit #{startIndex}, #{pageSize}

select r.id,s.sequence,s.name stuName,s.sex,s.phone stuPhone,t.workId,t.name tchName,t.phone tchPhone
from review r join student s on r.studentId=s.id join teacher t on r.teacherId=t.id
order by r.time desc limit #{startIndex}, #{pageSize}
