mapper.select:
select t.teacher_id,t.teacher_name, t.worknumber, t.sex, t.telephone, sc.major, sc.college, sc.classname
from teacher t
         join stuclass sc on t.teacher_id = sc.teacher_id
where worknumber = #{worknumber}
  and password = #{password}

select *
from teacher
order by teacher_id limit #{startIndex}, #{pageSize}

mapper.update:
update teacher
set password=#{chg}
where teacher_id = #{teacherId}
